$(document).on('click','.delete_withlist',function(event) {
    event.preventDefault();
    var id = $(this).data('id'); 
    var data = JSON.parse(localStorage.getItem('data'));
    if (data) {
        for(var i = 0; i < data.length; i++) {
            if(data[i].id == id) {
                data.splice(i,i);
                break;
            }
        }
        result = localStorage.setItem('data',JSON.stringify(data));
        window.location.reload();
  
    }
    if(data.length==1){
        for(var i = 0; i < data.length; i++) {
            if(data[i].id == id) {
                data.splice(i,1);
                break;
            }
        }
        result = localStorage.setItem('data',JSON.stringify(data));
        window.location.reload();
    }
  
    if(data[0]){
        for(var i = 0; i < data.length; i++) {
            if(data[i].id == id) {
                data.splice(i,1);
                break;
            }
        }
        result = localStorage.setItem('data',JSON.stringify(data));
        window.location.reload();
    }
});

